import React from 'react';
import { View, Text, Image, StyleSheet, FlatList, Dimensions } from 'react-native';

const PRODUCTS = [
  { id: '1', name: 'Sản phẩm 1', image: 'data:image/jpeg;base64,' },
  { id: '2', name: 'Sản phẩm 2', image: 'data:image/jpeg;base64,' },
  { id: '3', name: 'Sản phẩm 3', image: 'data:image/jpeg;base64,' },
  { id: '4', name: 'Sản phẩm 4', image: 'data:image/jpeg;base64,' },
  { id: '5', name: 'Sản phẩm 5', image: 'data:image/jpeg;base64,' },
  { id: '6', name: 'Sản phẩm 6', image: 'data:image/jpeg;base64,' },
  { id: '7', name: 'Sản phẩm 7', image: 'data:image/jpeg;base64,' },
  { id: '8', name: 'Sản phẩm 8', image: 'data:image/jpeg;base64,' },
];

const windowWidth = Dimensions.get('window').width;
const windowHeight = Dimensions.get('window').height;
const isTablet = windowWidth >= 768;
const isLandscape = windowWidth > windowHeight;

let numColumns = 2;
if (isTablet) {
  numColumns = 4;
} else if (isLandscape) {
  numColumns = 3;
}

const ITEM_MARGIN = 12;
const ITEM_WIDTH = (windowWidth - ITEM_MARGIN * (numColumns + 1)) / numColumns;
const ITEM_HEIGHT = ITEM_WIDTH * 1.2;
const FONT_SIZE = Math.round(ITEM_WIDTH / 9);

export default function Bai6() {
  return (
    <FlatList
      data={PRODUCTS}
      key={numColumns}
      numColumns={numColumns}
      keyExtractor={item => item.id}
      contentContainerStyle={styles.list}
      renderItem={({ item }) => (
        <View style={[styles.item, { width: ITEM_WIDTH, height: ITEM_HEIGHT }]}> 
          <Image source={{ uri: item.image }} style={styles.image} resizeMode="cover" />
          <Text style={[styles.name, { fontSize: FONT_SIZE }]} numberOfLines={2}>{item.name}</Text>
        </View>
      )}
    />
  );
}

const styles = StyleSheet.create({
  list: {
    padding: ITEM_MARGIN,
    backgroundColor: '#fff',
  },
  item: {
    backgroundColor: '#f3f4f6',
    borderRadius: 12,
    margin: ITEM_MARGIN / 2,
    alignItems: 'center',
    justifyContent: 'center',
    overflow: 'hidden',
    elevation: 2,
    shadowColor: '#000',
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.08,
    shadowRadius: 4,
  },
  image: {
    width: '100%',
    height: '70%',
    borderTopLeftRadius: 12,
    borderTopRightRadius: 12,
  },
  name: {
    marginTop: 8,
    fontWeight: 'bold',
    color: '#222',
    textAlign: 'center',
  },
});